<?php	if(isset($action)){		switch ($action) {			case $action:{				if(file_exists(PATH."view/catalogue/index.php")){					if($action){						$produit = $bdd->query("SELECT id, description, prix, image FROM produit WHERE idCategorie=:idcateg",array('idcateg' => substr($action, 0, 3)), PDO::FETCH_OBJ);					}else{						$produit = $bdd->query("SELECT id, description, prix, image FROM produit",null, PDO::FETCH_OBJ);					}					foreach($produit as $p){						htmlentities(addslashes($p->id));					}					require_once(PATH."view/catalogue/index.php");				}else{					header('Location: '.WEBROOT);				}				break;			}		}	}?>